
public class Atividade02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int total = 0;
		for (int contagem=1; contagem<=15; contagem++) {
			total += contagem;
		}
		System.out.println("o resultado da soma dos numeros de 1 a 15 é: " + total);
	}

}
